import React from 'react';
import { Link } from 'react-router-dom'; // Use Link for navigation
import { useAuth } from '../context/AuthContext';

function Navbar() {
  const { user, logout } = useAuth();

  return (
    <nav className="navbar d-flex align-items-center justify-content-between">
      {/* Logo */}
      <Link to="/" className="navbar-logo d-flex align-items-center">
        <img
          src="/assets/logo.png" /* Correct path to the logo */
          alt="Lawberate Logo"
          className="navbar-logo-img"
        />
        <span className="navbar-logo-text ms-2">Lawberate</span>
      </Link>

      {/* Navigation Links */}
      <div className="navbar-links d-flex align-items-center">
        <Link to="/blog">Blog</Link>
        <Link to="/faq">FAQ</Link>
        <Link to="/guides">Guides</Link>
        {user ? (
          <>
            <span className="ms-3">Welcome, {user.name}</span>
            <button onClick={logout} className="btn btn-secondary ms-3">Logout</button>
          </>
        ) : (
          <Link to="/login" className="btn btn-primary ms-3">Login</Link>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
