import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Simplify() {
  const [text, setText] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [feedback, setFeedback] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post('/api/analyze', { text });
      setSuggestions(response.data.suggestions);
      toast.success('Text analyzed successfully!');
    } catch (error) {
      toast.error('Error analyzing text. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/user/feedback', { feedback });
      setFeedback('');
      toast.success('Feedback submitted successfully!');
    } catch (error) {
      toast.error('Error submitting feedback. Please try again.');
    }
  };

  useEffect(() => {
    const fetchChatHistory = async () => {
      try {
        const response = await axios.get('/user/history');
        setChatHistory(response.data);
      } catch (error) {
        toast.error('Error fetching chat history.');
      }
    };
    fetchChatHistory();
  }, []);

  return (
    <div className="container mx-auto p-4">
      <ToastContainer />
      <h1 className="text-2xl font-bold mb-4">Simplify Legal Documents</h1>
      <form onSubmit={handleSubmit} className="mb-4">
        <textarea
          className="w-full p-4 border rounded-lg shadow-md mb-4 focus:outline-none focus:ring-2 focus:ring-blue-500"
          rows="6"
          placeholder="Paste your text here..."
          value={text}
          onChange={handleTextChange}
          aria-label="Text to simplify"
        ></textarea>
        <button
          type="submit"
          className="btn btn-primary"
          disabled={loading}
        >
          {loading ? 'Simplifying...' : 'Simplify'}
        </button>
      </form>
      {suggestions.length > 0 && (
        <div className="mb-4">
          <h2 className="text-xl font-semibold">Suggestions</h2>
          <ul className="list-disc pl-5">
            {suggestions.map((suggestion, index) => (
              <li key={index}>{suggestion}</li>
            ))}
          </ul>
        </div>
      )}
      <form onSubmit={handleFeedbackSubmit} className="mb-4">
        <textarea
          className="w-full p-4 border rounded-lg shadow-md mb-4 focus:outline-none focus:ring-2 focus:ring-blue-500"
          rows="3"
          placeholder="Provide feedback..."
          value={feedback}
          onChange={(e) => setFeedback(e.target.value)}
          aria-label="Feedback"
        ></textarea>
        <button type="submit" className="btn btn-secondary">Submit Feedback</button>
      </form>
      <div>
        <h2 className="text-xl font-semibold">Chat History</h2>
        <ul className="list-disc pl-5">
          {chatHistory.map((chat, index) => (
            <li key={index}>{chat.original} - {chat.simplified}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Simplify;
