import React, { useState } from 'react';
import { FaPaperclip } from 'react-icons/fa'; // Import pin icon

function SimplifyTool() {
  const [inputText, setInputText] = useState('');
  const [outputText, setOutputText] = useState('');
  const [file, setFile] = useState(null);

  const handleSimplify = () => {
    // Simulate simplification logic
    setOutputText(`Simplified: ${inputText}`);
  };

  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    if (uploadedFile) {
      setFile(uploadedFile);
      setInputText(uploadedFile.name); // Display file name in the input box
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Simplifying Tool</h1>
      <div className="row">
        <div className="col-md-6 position-relative">
          <textarea
            className="form-control"
            rows="10"
            placeholder="Paste your text here..."
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            readOnly={!!file} // Make read-only if a file is selected
          ></textarea>
          <label htmlFor="file-upload" className="file-upload-icon position-absolute" style={{ right: '10px', top: '10px', cursor: 'pointer' }}>
            <FaPaperclip />
          </label>
          <input
            id="file-upload"
            type="file"
            className="d-none"
            onChange={handleFileChange}
          />
        </div>
        <div className="col-md-6">
          <textarea
            className="form-control"
            rows="10"
            placeholder="Simplified text will appear here..."
            value={outputText}
            readOnly
          ></textarea>
        </div>
      </div>
      <div className="text-center mt-4">
        <button className="btn btn-primary" onClick={handleSimplify}>
          Simplify
        </button>
      </div>
    </div>
  );
}

export default SimplifyTool;
