// src/pages/Home.jsx
import React, { useState, useRef } from 'react';
import { MdClose } from 'react-icons/md'; // Import close icon for file preview

function Home() {
  const [text, setText] = useState('');
  const [file, setFile] = useState(null);
  const simplifySectionRef = useRef(null);

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    if (uploadedFile) {
      setFile(uploadedFile);
    }
  };

  const removeFile = () => {
    setFile(null);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Text submitted:', text);
    console.log('File uploaded:', file);
    // Add API integration for text and file
  };

  const scrollToSimplifySection = () => {
    simplifySectionRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="home-page">
      {/* Hero Section */}
      <div className="hero-section d-flex align-items-center">
        <div className="container">
          <div className="row">
            <div className="col-md-6">
              <h1 className="hero-title">Simplify Legal Documents with <span className="text-highlight">Ease</span></h1>
              <p className="hero-description">
                Lawberate uses AI to simplify legal documents, identify risks, and boost productivityâ€”without sacrificing accuracy.
              </p>
              <button className="btn btn-primary" onClick={scrollToSimplifySection}>
                Get Started for Free
              </button>
            </div>
            <div className="col-md-6">
              <img
                src="/assets/custom-hero-illustration.svg" /* Replace with your custom illustration */
                alt="Hero Illustration"
                className="img-fluid"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Simplify Your Documents Section */}
      <div ref={simplifySectionRef} className="simplify-section container text-center mt-5">
        <h2 className="simplify-title">Simplify Your Documents</h2>
        <p className="simplify-description">
          Paste your text or upload a document to simplify it with our AI-powered platform.
        </p>
        <form onSubmit={handleSubmit} className="mt-4">
          <textarea
            className="form-control mb-3"
            rows="6"
            placeholder="Paste your text here..."
            value={text}
            onChange={handleTextChange}
          ></textarea>
          <div className="file-upload-container">
            <label htmlFor="file-upload" className="btn btn-upload">
              Upload File
            </label>
            <input
              id="file-upload"
              type="file"
              className="d-none"
              onChange={handleFileChange}
            />
            {file && (
              <div className="file-preview">
                <span>{file.name}</span>
                <MdClose className="file-remove-icon" onClick={removeFile} />
              </div>
            )}
          </div>
          <button type="submit" className="btn btn-primary mt-3">Simplify</button>
        </form>
      </div>
    </div>
  );
}

export default Home;
