// src/pages/Home.jsx
import React, { useState } from 'react';

function Home() {
  const [text, setText] = useState('');

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Text submitted:', text);
    // Add API integration for text
  };

  return (
    <div className="home-page">
      {/* Hero Section */}
      <div className="hero-section d-flex align-items-center">
        <div className="container">
          <div className="row">
            <div className="col-md-6">
              <h1 className="hero-title">Your ideas, <span className="text-highlight">better simplified</span></h1>
              <p className="hero-description">
                We use AI to simplify legal documents and boost productivityâ€”without sacrificing accuracy.
              </p>
              <div className="hero-cta">
                <button className="btn btn-primary">Get Started for Free</button>
              </div>
            </div>
            <div className="col-md-6">
              <img
                src="/assets/hero-illustration.svg" /* Replace with your image path */
                alt="Hero Illustration"
                className="img-fluid"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Simplify Your Documents Section */}
      <div className="simplify-section container text-center mt-5">
        <h2 className="simplify-title">Simplify Your Documents</h2>
        <p className="simplify-description">
          Paste your text to simplify it with our AI-powered platform.
        </p>
        <form onSubmit={handleSubmit} className="mt-4">
          <textarea
            className="form-control mb-3"
            rows="6"
            placeholder="Paste your text here..."
            value={text}
            onChange={handleTextChange}
          ></textarea>
          <button type="submit" className="btn btn-primary">Simplify</button>
        </form>
      </div>
    </div>
  );
}

export default Home;
