import React, { useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { fetchHistory } from './store/historySlice';
import './index.css';
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import Home from './pages/Home';
import Blog from './pages/Blog';
import BlogPost from './pages/BlogPost';
import FAQ from './pages/FAQ';
import Guide from './pages/Guide';
import Login from './pages/Login';
import Register from './pages/Register';
import { useAuth } from './context/AuthContext';

function App() {
  const dispatch = useDispatch();
  const { user, isGuest, login, logout, guestAttempts } = useAuth();
  const { history, loading, error } = useSelector((state) => state.history);

  useEffect(() => {
    console.log('Redux State:', { history, loading, error }); // Debug log

    // Only fetch history if the user exists and the history is not already loaded
    if (user && !loading && !error && history.length === 0) {
      dispatch(fetchHistory(user.token));
    }
  }, [user, dispatch, history, loading, error]); // Include all dependencies

  console.log('History:', history); // Debug log

  const guestAttemptHandler = () => {
    if (guestAttempts > 0) {
      console.log('Guest attempt used');
    } else {
      alert('You have used all your free attempts. Please register or log in to continue.');
    }
  };

  const handleSelect = (entry) => {
    if (isGuest()) {
      guestAttemptHandler();
      console.log('Selected history entry:', entry);
    } else {
      console.log('Selected history entry:', entry);
    }
  };

  return (
    <>
      <Navbar />
      <div className="flex">
        {(user || isGuest()) && (
          <Sidebar
            isAuthenticated={!!user}
            history={history}
            onSelect={handleSelect}
            onLogout={logout}
          />
        )}
        <main className="flex-1 p-6 bg-light-gray">
          <Routes>
            <Route path="/" element={user || isGuest() ? <Home /> : <Navigate to="/login" />} />
            <Route path="/blog" element={user || isGuest() ? <Blog /> : <Navigate to="/login" />} />
            <Route path="/blog/:id" element={user || isGuest() ? <BlogPost /> : <Navigate to="/login" />} />
            <Route path="/faq" element={<FAQ />} />
            <Route path="/guide" element={<Guide />} />
            <Route path="/login" element={<Login onLogin={login} />} />
            <Route path="/register" element={<Register />} />
            {/* Fallback route */}
            <Route path="*" element={<div>Page not found</div>} />
          </Routes>
        </main>
      </div>
    </>
  );
}

export default App;
