// src/pages/Home.jsx
import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import ErrorMessage from '../components/ErrorMessage';

function Home() {
  console.log('Rendering Home component'); // Debug log
  const { user, isGuest, guestAttempts, guestAttemptHandler } = useAuth();
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    const allowedTypes = ['application/pdf', 'application/msword', 'image/png', 'image/jpeg'];
    const maxSize = 5 * 1024 * 1024; // 5 MB

    if (!allowedTypes.includes(selectedFile.type)) {
      alert('Unsupported file type. Please upload a PDF, Word, or image file.');
      return;
    }

    if (selectedFile.size > maxSize) {
      alert('File size exceeds the 5MB limit. Please upload a smaller file.');
      return;
    }

    setFile(selectedFile);
  };

  const handleSimplify = async () => {
    console.log('Simplify button clicked'); // Debug log
    if (!file) {
      alert('Please upload a file before proceeding.');
      return;
    }

    if (!user) {
      console.log('Guest user attempting to simplify'); // Debug log
      guestAttemptHandler();
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('https://api.lawberate.com/simplify', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${user?.token || ''}`,
        },
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Failed to simplify document');
      }

      const data = await response.json();
      console.log('Simplified data:', data); // Debug log
      setResult({
        simplifiedContent: data.simplifiedContent,
        risks: data.risks || [],
      });

      if (isGuest() && guestAttempts > 0) {
        guestAttemptHandler(); // Deduct a guest attempt
      }
    } catch (error) {
      console.error('Failed to simplify document:', error);
      setError('Unable to process your document at this time. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="home-page">
      <h1 className="text-lg font-bold mb-4">Simplify Legal Documents</h1>
      <input type="file" onChange={handleFileChange} />
      <button
        onClick={handleSimplify}
        className="btn btn-primary mt-4"
        disabled={loading}
      >
        {loading ? 'Processing...' : 'Simplify Document'}
      </button>
      {error && <ErrorMessage message={error} onRetry={handleSimplify} />}
      {file && (
        <div className="result mt-4">
          <h2 className="text-md font-bold">Original Document:</h2>
          <p>{file?.name}</p>
        </div>
      )}
      {result && (
        <div className="result mt-4">
          <h2 className="text-md font-bold mt-4">Simplified Content:</h2>
          <p>{result.simplifiedContent}</p>
          {result.risks.length > 0 && (
            <div>
              <h2 className="text-md font-bold mt-4 text-red-500">Highlighted Risks:</h2>
              <ul className="list-disc pl-6">
                {result.risks.map((risk, index) => (
                  <li key={index}>{risk}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default Home;
