// src/pages/Home.jsx
import React, { useState, useRef } from 'react';
import { FaPaperclip } from 'react-icons/fa'; // Import pin icon

function Home() {
  const [text, setText] = useState('');
  const [file, setFile] = useState(null);
  const simplifySectionRef = useRef(null);

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    console.log('File uploaded:', e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Text submitted:', text);
    console.log('File uploaded:', file);
    // Add API integration for text and file
  };

  const scrollToSimplifySection = () => {
    simplifySectionRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="home-page">
      {/* Hero Section */}
      <div className="hero-section d-flex align-items-center">
        <div className="container">
          <div className="row">
            <div className="col-md-6">
              <h1 className="hero-title">Your ideas, <span className="text-highlight">better simplified</span></h1>
              <p className="hero-description">
                We use AI to simplify legal documents and boost productivityâ€”without sacrificing accuracy.
              </p>
              <div className="hero-cta">
                <button className="btn btn-primary" onClick={scrollToSimplifySection}>
                  Get Started for Free
                </button>
              </div>
            </div>
            <div className="col-md-6">
              <img
                src="/assets/hero-illustration.svg" /* Replace with your image path */
                alt="Hero Illustration"
                className="img-fluid"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Simplify Your Documents Section */}
      <div ref={simplifySectionRef} className="simplify-section container text-center mt-5">
        <h2 className="simplify-title">Simplify Your Documents</h2>
        <p className="simplify-description">
          Paste your text or upload a document to simplify it with our AI-powered platform.
        </p>
        <form onSubmit={handleSubmit} className="mt-4 d-flex align-items-center justify-content-center">
          <textarea
            className="form-control"
            rows="6"
            placeholder="Paste your text here..."
            value={text}
            onChange={handleTextChange}
          ></textarea>
          <label htmlFor="file-upload" className="file-upload-label ms-3">
            <FaPaperclip className="file-upload-icon" />
          </label>
          <input
            id="file-upload"
            type="file"
            className="d-none"
            onChange={handleFileChange}
          />
          <button type="submit" className="btn btn-primary ms-3">Simplify</button>
        </form>
      </div>
    </div>
  );
}

export default Home;
