// src/pages/Home.jsx
import React, { useState, useRef } from 'react';
import { FaPaperclip } from 'react-icons/fa'; // Import pin icon
import { Button, Input, Typography, Card } from 'antd'; // Use Ant Design components

const { TextArea } = Input;
const { Title, Paragraph } = Typography;

function Home() {
  const [text, setText] = useState('');
  const [file, setFile] = useState(null);
  const simplifySectionRef = useRef(null);

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    console.log('File uploaded:', e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Text submitted:', text);
    console.log('File uploaded:', file);
    // Add API integration for text and file
  };

  const scrollToSimplifySection = () => {
    simplifySectionRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="home-page">
      {/* Hero Section */}
      <div className="hero-section d-flex align-items-center">
        <div className="container">
          <div className="row">
            <div className="col-md-6">
              <Title level={1}>
                Your ideas, <span className="text-highlight">better simplified</span>
              </Title>
              <Paragraph>
                We use AI to simplify legal documents and boost productivityâ€”without sacrificing accuracy.
              </Paragraph>
              <Button type="primary" size="large" onClick={scrollToSimplifySection}>
                Get Started for Free
              </Button>
            </div>
            <div className="col-md-6">
              <img
                src="/assets/hero-illustration.svg" /* Replace with your image path */
                alt="Hero Illustration"
                className="img-fluid"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Simplify Your Documents Section */}
      <div ref={simplifySectionRef} className="simplify-section container text-center mt-5">
        <Title level={2}>Simplify Your Documents</Title>
        <Paragraph>
          Paste your text or upload a document to simplify it with our AI-powered platform.
        </Paragraph>
        <Card className="mt-4" style={{ maxWidth: '800px', margin: '0 auto', padding: '20px' }}>
          <form onSubmit={handleSubmit} className="d-flex align-items-center">
            <TextArea
              rows={6}
              placeholder="Paste your text here..."
              value={text}
              onChange={handleTextChange}
              style={{ flex: 1, marginRight: '10px' }}
            />
            <label htmlFor="file-upload" className="file-upload-label">
              <FaPaperclip className="file-upload-icon" />
            </label>
            <input
              id="file-upload"
              type="file"
              className="d-none"
              onChange={handleFileChange}
            />
            <Button type="primary" htmlType="submit" style={{ marginLeft: '10px' }}>
              Simplify
            </Button>
          </form>
        </Card>
      </div>
    </div>
  );
}

export default Home;
