import React from 'react';
import ReactDOM from 'react-dom/client';
import { Provider } from 'react-redux';
import { BrowserRouter } from 'react-router-dom';
import { store } from './store';
import { AuthProvider } from './context/AuthContext';
import App from './App';
import ErrorBoundary from './components/ErrorBoundary';
import './index.css';

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <ErrorBoundary>
      <Provider store={store}>
        <AuthProvider>
          <BrowserRouter future={{ v7_startTransition: true, v7_relativeSplatPath: true }}>
            <App />
          </BrowserRouter>
        </AuthProvider>
      </Provider>
    </ErrorBoundary>
  </React.StrictMode>
);

document.addEventListener('DOMContentLoaded', () => {
  const sidebar = document.querySelector('.sidebar');
  if (!sidebar) {
    console.error('Sidebar element not found.');
    return;
  }

  const toggleButton = document.createElement('button');
  toggleButton.className = 'sidebar-toggle-btn';
  toggleButton.textContent = '☰'; // Hamburger icon
  document.body.appendChild(toggleButton); // Ensure it's appended to the body

  toggleButton.addEventListener('click', () => {
    if (sidebar.classList.contains('sidebar-expanded')) {
      sidebar.classList.remove('sidebar-expanded');
      sidebar.classList.add('sidebar-collapsed');
      toggleButton.textContent = '☰'; // Show hamburger icon
    } else {
      sidebar.classList.remove('sidebar-collapsed');
      sidebar.classList.add('sidebar-expanded');
      toggleButton.textContent = '×'; // Show close icon
    }
  });
});
