// src/pages/Home.jsx
import React, { useState } from 'react';

function Home() {
  const [isTextMode, setIsTextMode] = useState(true);
  const [text, setText] = useState('');
  const [file, setFile] = useState(null);

  const handleToggleChange = () => {
    setIsTextMode(!isTextMode);
  };

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isTextMode) {
      console.log('Text submitted:', text);
      // Add API integration for text
    } else {
      console.log('File uploaded:', file);
      // Add API integration for file
    }
  };

  return (
    <div className="home-page">
      {/* Hero Section */}
      <div className="hero-section d-flex align-items-center">
        <div className="container">
          <div className="row">
            <div className="col-md-6">
              <h1 className="hero-title">Your ideas, <span className="text-highlight">better simplified</span></h1>
              <p className="hero-description">
                We use AI to simplify legal documents and boost productivityâ€”without sacrificing accuracy.
              </p>
              <div className="hero-cta">
                <button className="btn btn-primary">Get Started for Free</button>
              </div>
            </div>
            <div className="col-md-6">
              <img
                src="/assets/hero-illustration.svg" /* Replace with your image path */
                alt="Hero Illustration"
                className="img-fluid"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Simplify Your Documents Section */}
      <div className="simplify-section container text-center mt-5">
        <h2 className="simplify-title">Simplify Your Documents</h2>
        <p className="simplify-description">
          Use the toggle below to switch between pasting text or uploading a document.
        </p>
        <div className="toggle-container d-flex justify-content-center align-items-center mt-4">
          <label className="toggle-label me-3">Paste Text</label>
          <div className="form-check form-switch">
            <input
              className="form-check-input"
              type="checkbox"
              id="toggleMode"
              checked={!isTextMode}
              onChange={handleToggleChange}
            />
          </div>
          <label className="toggle-label ms-3">Upload File</label>
        </div>
        <form onSubmit={handleSubmit} className="mt-4">
          {isTextMode ? (
            <textarea
              className="form-control"
              rows="6"
              placeholder="Paste your text here..."
              value={text}
              onChange={handleTextChange}
              required
            ></textarea>
          ) : (
            <input
              type="file"
              className="form-control-file"
              onChange={handleFileChange}
              required
            />
          )}
          <button type="submit" className="btn btn-primary mt-3">Simplify</button>
        </form>
      </div>

      {/* Features Section */}
      <div className="features-section container text-center mt-5">
        <h2 className="features-title">Eight tools. <span className="text-highlight">One platform.</span></h2>
        <div className="row mt-4">
          <div className="col-md-3">
            <div className="feature-card">
              <h3>Document Simplifier</h3>
              <p>Make legal documents easy to understand.</p>
            </div>
          </div>
          <div className="col-md-3">
            <div className="feature-card">
              <h3>Risk Identifier</h3>
              <p>Highlight potential risks in contracts.</p>
            </div>
          </div>
          <div className="col-md-3">
            <div className="feature-card">
              <h3>AI-Powered Insights</h3>
              <p>Get actionable insights from legal text.</p>
            </div>
          </div>
          <div className="col-md-3">
            <div className="feature-card">
              <h3>Collaboration Tools</h3>
              <p>Work with your team seamlessly.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
