import React, { createContext, useContext, useState, useMemo, useCallback } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [guestAttempts, setGuestAttempts] = useState(3);

  const login = useCallback((userData) => {
    setUser(userData);
  }, []);

  const logout = useCallback(() => {
    setUser(null);
  }, []);

  const isGuest = useCallback(() => !user, [user]);

  const guestAttemptHandler = useCallback(() => {
    if (guestAttempts > 0) {
      setGuestAttempts((prev) => prev - 1);
    } else {
      alert('You have used all your free attempts. Please register or log in to continue.');
    }
  }, [guestAttempts]);

  const value = useMemo(() => ({
    user,
    isGuest,
    login,
    logout,
    guestAttempts,
    guestAttemptHandler,
  }), [user, isGuest, login, logout, guestAttempts, guestAttemptHandler]);

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  console.log('Auth Context:', context); // Debug log
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};
