import React, { useState } from 'react';

function SimplifyTool() {
  const [inputText, setInputText] = useState('');
  const [outputText, setOutputText] = useState('');

  const handleSimplify = () => {
    // Simulate simplification logic
    setOutputText(`Simplified: ${inputText}`);
  };

  return (
    <div className="simplify-container container mt-5">
      <h1 className="text-center mb-4">Simplify Your Text</h1>
      <div className="simplify-tool d-flex justify-content-between">
        {/* Input Box */}
        <textarea
          className="form-control simplify-input"
          rows="10"
          placeholder="Paste your text here..."
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
        ></textarea>

        {/* Output Box */}
        <textarea
          className="form-control simplify-output"
          rows="10"
          placeholder="Simplified text will appear here..."
          value={outputText}
          readOnly
        ></textarea>
      </div>

      {/* Simplify Button */}
      <div className="text-center mt-4">
        <button className="btn btn-primary simplify-button" onClick={handleSimplify}>
          Simplify
        </button>
      </div>
    </div>
  );
}

export default SimplifyTool;
