import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Simplify() {
  const [text, setText] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [feedback, setFeedback] = useState('');
  const [chatHistory, setChatHistory] = useState([]);

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/analyze', { text });
      setSuggestions(response.data.suggestions);
    } catch (error) {
      console.error('Error analyzing text:', error);
    }
  };

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/user/feedback', { feedback });
      setFeedback('');
    } catch (error) {
      console.error('Error submitting feedback:', error);
    }
  };

  useEffect(() => {
    const fetchChatHistory = async () => {
      try {
        const response = await axios.get('/user/history');
        setChatHistory(response.data);
      } catch (error) {
        console.error('Error fetching chat history:', error);
      }
    };
    fetchChatHistory();
  }, []);

  return (
    <div className="container">
      <h1 className="mt-4">Simplify Legal Documents</h1>
      <form onSubmit={handleSubmit} className="mt-4">
        <textarea
          className="form-control mb-3"
          rows="6"
          placeholder="Paste your text here..."
          value={text}
          onChange={handleTextChange}
        ></textarea>
        <button type="submit" className="btn btn-primary">Simplify</button>
      </form>
      {suggestions.length > 0 && (
        <div className="mt-4">
          <h2>Suggestions</h2>
          <ul>
            {suggestions.map((suggestion, index) => (
              <li key={index}>{suggestion}</li>
            ))}
          </ul>
        </div>
      )}
      <form onSubmit={handleFeedbackSubmit} className="mt-4">
        <textarea
          className="form-control mb-3"
          rows="3"
          placeholder="Provide feedback..."
          value={feedback}
          onChange={(e) => setFeedback(e.target.value)}
        ></textarea>
        <button type="submit" className="btn btn-secondary">Submit Feedback</button>
      </form>
      <div className="mt-4">
        <h2>Chat History</h2>
        <ul>
          {chatHistory.map((chat, index) => (
            <li key={index}>{chat.original} - {chat.simplified}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Simplify;
